const form=document.getElementById("form"),username=document.getElementById("username"),email=document.getElementById("email"),channel=document.getElementById("channel"),submit=document.getElementById("channel_name");document.querySelector(".submit");const url=document.getElementById("url");function checkInputs(){const e=username.value.trim(),t=email.value.trim(),n=channel.value.trim(),r=url.value.trim();""===e?setErrorFor(username,"Username cannot be emptry"):isName(e)?setSuccessFor(username):setErrorFor(username,"Keep spaces"),""===t?setErrorFor(email,"Email cannot be empty"):isEmail(t)?setSuccessFor(email):setErrorFor(email,"Not a valid email"),""===n?setErrorFor(channel,"Channel cannot be empty"):n>=25?setErrorFor(channel,"Channel name limited to 25 characters"):setSuccessFor(channel),""===n?setErrorFor(channel,"Channel details be empty"):n>=25?setErrorFor(channel,"Channel details limited to 25 characters"):setSuccessFor(channel),""===r?setErrorFor(url,"Url cannot be empty"):isURL(r)?setSuccessFor(url):setErrorFor(url,"Url is not valid")}function setErrorFor(e,t){const n=e.parentElement,r=n.querySelector("small");n.className="form-control error",r.innerText=t}function setSuccessFor(e){e.parentElement.className="form-control success"}function isEmail(e){return/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e)}function isURL(e){return new RegExp("^(https?:\\/\\/)?((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.?)+[a-z]{2,}|((\\d{1,3}\\.){3}\\d{1,3}))(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*(\\?[;&a-z\\d%_.~+=-]*)?(\\#[-a-z\\d_]*)?$","i").test(e)}function isName(e){return/^[A-Za-z ]+$/.test(e)}submit.addEventListener("click",e=>{e.preventDefault(),checkInputs()});const floating_btn=document.querySelector(".floating-btn"),close_btn=document.querySelector(".close-btn"),social_panel_container=document.querySelector(".social-panel-container");floating_btn.addEventListener("click",()=>{social_panel_container.classList.toggle("visible")}),close_btn.addEventListener("click",()=>{social_panel_container.classList.remove("visible")});